import React from "react";
import { useNavigate } from "react-router-dom";
import Button from "./button";
function TableComp(props) {
  const navigate = useNavigate();
  let logedinUser = JSON.parse(localStorage.getItem("logedinUser"));
  let oldUserData=JSON.parse(localStorage.getItem("deleteUserData"));
  const handleDeleteUser = (user) => {
    localStorage.setItem("deleteUserData", JSON.stringify(user));
    console.log(user);
    let oldUserIndex;
    let userArr=JSON.parse(localStorage.getItem(userArr));
    for (const [index,user] of userArr.entries()) {
      console.log(user.email);
      if(user.email===oldUserData.email){
        oldUserIndex=index;
      }
    }
    console.log(oldUserIndex);
    props.userArr.splice[oldUserIndex]=0;
  };
  const handleEditUser = (user) => {
    localStorage.setItem("editUserData", JSON.stringify(user));
    navigate("/dasboard/update");
    console.log(user);
  };
  return (
    <form>
      <table>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {props.userArr.map((user, index) => (
            <tr key={user.name}>
              <td>{index + 1}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                {logedinUser.role === "Admin" ||
                logedinUser.role === "Manager" ? (
                  <Button label="Edit" onClick={() => handleEditUser(user)} />
                ) : (
                  ""
                )}
                {logedinUser.role === "Admin" ? (
                  <Button
                    label="Delete"
                    onClick={() => handleDeleteUser(user)}
                  />
                ) : (
                  ""
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </form>
  );
}

export default TableComp;
