import React, { useState } from "react";
import Textfield from "../components/common/textfield";
import Button from "../components/common/button";
function Update() {
  let oldUserData = JSON.parse(localStorage.getItem("editUserData"));
  const [oldNameState, setNameState] = useState(oldUserData.name);
  const [oldEmailState, setEmailState] = useState(oldUserData.email);
  const editedUser = () => {
    let name = document.getElementById("name").value;
    let email = document.getElementById("email").value;
    let userArr = JSON.parse(localStorage.getItem("userArr"));
    console.log(userArr);
    console.log(name);
    console.log(email);
    let currentUser =  userArr.map((user, index) => {
      let currentEle;
      if (user.email === oldEmailState) {
        localStorage.removeItem("userArr")
        currentEle = user
        }
        return currentEle;
    });
    currentUser.name=name;
    currentUser.email=email;
    
    console.log(currentUser,'currentUser');
  };
  return (
    <div>
      <Textfield
        id={"name"}
        label={"Name"}
        type={"text"}
        value={oldNameState}
        onChange={(e) => {
          setNameState(e.target.value);
        }}
      />{" "}
      <br />
      <Textfield
        id={"email"}
        label={"Email"}
        type={"text"}
        value={oldEmailState}
        onChange={(e) => {
          setEmailState(e.target.value);
        }}
      />{" "}
      <br />
      <Button label={"Update"} onClick={editedUser} /> <br />
    </div>
  );
}

export default Update;
