import React, { useState, useEffect } from "react";
import Textfield from "../components/common/textfield";
import NativeSelect from "../components/Select";
import Button from "../components/common/button";
function Update() {
  const [Role, setRole] = useState(null);
  const [oldUserDatatState, setOldUserState] = useState(null);
  

  useEffect(() => {
    let oldUserData = JSON.parse(localStorage.getItem("editUserData"));
    setOldUserState(oldUserData);
    console.log(oldUserData);
  }, []);

  function updateUser() {}

  let rolechange = (data) => {
    console.log(data);
    setRole(data.target.value);
  };

  if (oldUserDatatState) {
    return (
      <div>
        <Textfield
          id={"name"}
          label={"Name"}
          type={"text"}
          value={oldUserDatatState.name}
        />{" "}
        <br />
        <Textfield
          id={"email"}
          label={"Email"}
          type={"text"}
          value={oldUserDatatState.email}
        />{" "}
        <br />
        <NativeSelect onChange={rolechange} />
        <Button label={"Update"} onClick={updateUser} /> <br />
      </div>
    );
  } else {
    return <div>Loading......</div>;
  }
}

export default Update;
